package estrutura_de_repetições2;

public class ex24w {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int  i, l, total;
		
		i = 5;
		l = 1;
		
		while((l <= 10)) {
		
			total = i * l;
			System.out.println("5 x " + l + " = " + total );
			l++;
			
		}
		
		
		
		
		
		
	}

}
